import React, { useState, useEffect, useRef } from 'react';
import DailyIframe from '@daily-co/daily-js';

function App() {
  const [callFrame, setCallFrame] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [error, setError] = useState(null);
  const videoContainerRef = useRef(null);

  useEffect(() => {
    return () => {
      // Clean up the call when component unmounts
      if (callFrame) {
        callFrame.destroy();
      }
    };
  }, [callFrame]);

  const subscribeToRoom = async (roomId) => {
    try {
      if (callFrame) {
        callFrame.destroy();
      }

      setError(null);

      const daily = DailyIframe.createFrame(videoContainerRef.current, {
        iframeStyle: {
          width: '100%',
          height: '100%',
          border: 'none',
          backgroundColor: 'transparent',
        },
        showLeaveButton: true,
        url: `https://tavus.daily.co/${roomId}`,
      });

      // Set up event handlers
      daily.on('participant-joined', (event) => {
        console.log('Participant joined:', event.participant);
        updateParticipants(daily);
      });

      daily.on('participant-left', () => {
        updateParticipants(daily);
      });

      daily.on('participant-updated', () => {
        updateParticipants(daily);
      });

      daily.on('error', (error) => {
        console.error('Daily.co error:', error);
        setError(error.errorMsg || 'An error occurred with the video call');
      });

      // Wait for the frame to be loaded
      await daily.load();

      // Join with the correct configuration
      await daily.join({
        subscribeToTracksAutomatically: true,
        audio: false,
        video: false,
      });

      setCallFrame(daily);
    } catch (err) {
      console.error('Error subscribing to room:', err);
      setError(err.message || 'Failed to subscribe to the room');
    }
  };

  const updateParticipants = (daily) => {
    try {
      const participantData = daily.participants();
      if (participantData) {
        const participantList = Object.values(participantData);
        setParticipants(participantList);
      }
    } catch (err) {
      console.error('Error updating participants:', err);
    }
  };

  return (
    <div className="app">
      <div className="controls">
        <input 
          type="text" 
          placeholder="Enter room ID" 
          onKeyPress={(e) => e.key === 'Enter' && subscribeToRoom(e.target.value)}
        />
        <button onClick={(e) => subscribeToRoom(document.querySelector('input').value)}>
          Subscribe to Room
        </button>
      </div>

      {error && (
        <div className="error-message" style={{ color: 'red', marginBottom: '10px' }}>
          {error}
        </div>
      )}

      <div className="video-container" ref={videoContainerRef} style={{ width: '800px', height: '600px' }}></div>

      <div className="participants-list">
        <h3>Participants ({participants.length})</h3>
        <ul>
          {participants.map((participant) => (
            <li key={participant.session_id}>
              {participant.user_name || 'Guest'} 
              {participant.local ? ' (You)' : ''}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
