import React, { useState, useEffect, useRef } from 'react';
import DailyJs from '@daily-co/daily-js';

function App() {
  const [callObject, setCallObject] = useState(null);
  const [participants, setParticipants] = useState({});
  const [error, setError] = useState(null);
  const videoRefs = useRef({});

  useEffect(() => {
    return () => {
      if (callObject) {
        callObject.destroy();
      }
    };
  }, [callObject]);

  const subscribeToRoom = async (roomId) => {
    try {
      if (callObject) {
        callObject.destroy();
      }

      setError(null);

      // Create a new call object
      const daily = DailyJs.createCallObject({
        audioSource: true,
        videoSource: true,
        subscribeToTracksAutomatically: false, // Disable automatic subscriptions
      });

      // Set up event handlers
      daily.on('participant-joined', handleParticipantJoined);
      daily.on('participant-left', handleParticipantLeft);
      daily.on('participant-updated', handleParticipantUpdated);
      daily.on('error', handleError);

      // Join the room
      await daily.join({
        url: `https://tavus.daily.co/${roomId}`,
      });

      setCallObject(daily);
    } catch (err) {
      console.error('Error subscribing to room:', err);
      setError(err.message || 'Failed to subscribe to the room');
    }
  };

  const handleParticipantJoined = (event) => {
    const { participant } = event;
    console.log('Participant joined:', participant);
    updateParticipants();
  };

  const handleParticipantLeft = (event) => {
    const { participant } = event;
    console.log('Participant left:', participant);
    updateParticipants();
  };

  const handleParticipantUpdated = (event) => {
    const { participant } = event;
    console.log('Participant updated:', participant);
    updateParticipants();
  };

  const handleError = (error) => {
    console.error('Daily.co error:', error);
    setError(error.errorMsg || 'An error occurred with the video call');
  };

  const updateParticipants = () => {
    if (callObject) {
      const participantData = callObject.participants();
      setParticipants(participantData);

      // Subscribe to tracks for each participant
      Object.values(participantData).forEach(participant => {
        if (!participant.local) {
          // Subscribe to video and audio tracks
          callObject.updateParticipant(participant.session_id, {
            setSubscribed: {
              video: true,
              audio: true
            }
          });
        }
      });
    }
  };

  const attachVideo = (participantId, track) => {
    if (videoRefs.current[participantId]) {
      const videoElement = videoRefs.current[participantId];
      if (track) {
        track.attach(videoElement);
      } else {
        // Detach if track is null
        videoElement.srcObject = null;
      }
    }
  };

  useEffect(() => {
    // Update video elements when participants change
    Object.entries(participants).forEach(([participantId, participant]) => {
      if (participant.video) {
        attachVideo(participantId, participant.video.track);
      }
      if (participant.audio) {
        // Handle audio track if needed
        console.log('Audio track available for participant:', participantId);
      }
    });
  }, [participants]);

  return (
    <div className="app">
      <div className="controls">
        <input 
          type="text" 
          placeholder="Enter room ID" 
          onKeyPress={(e) => e.key === 'Enter' && subscribeToRoom(e.target.value)}
        />
        <button onClick={(e) => subscribeToRoom(document.querySelector('input').value)}>
          Join Room
        </button>
      </div>

      {error && (
        <div className="error-message" style={{ color: 'red', marginBottom: '10px' }}>
          {error}
        </div>
      )}

      <div className="video-grid">
        {Object.entries(participants).map(([participantId, participant]) => (
          <div key={participantId} className="video-container">
            <video
              ref={el => videoRefs.current[participantId] = el}
              autoPlay
              playsInline
              muted={participant.local}
              style={{ width: '100%', height: '100%', objectFit: 'cover' }}
            />
            <div className="participant-name">
              {participant.user_name || 'Guest'} 
              {participant.local ? ' (You)' : ''}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
